mapper.insert:
INSERT INTO USERDB(
    USER_CODE
    , USER_ID
    , USER_PWD
    , USER_NAME
    , USER_PHONE
    , USER_ROLE
    , USER_PIC
    , USER_GENDER
    , USER_BIRTH
    , USER_ADDRESS
    , USER_ETC
)
VALUES(
    #{ code }
    , #{ id }
    , #{ pwd }
    , #{ name }
    , #{ phone }
    , #{ role }
    , #{ pic }
    , #{ gender }
    , #{ birth }
    , #{ address }
    , #{ etc }
)

INSERT INTO PHYSICALDB(
    USER_CODE
    , PHY_HEIGHT
    , PHY_WEIGHT
    , PHY_BMI
)
VALUES(
    #{ code }
    , #{ height }
    , #{ weight }
    , #{ bmi }
)

INSERT INTO EMPLOYEEDB(
    USER_CODE
    , EMP_LOCATION
    , EMP_QUAL
    , EMP_SALARY
    , EMP_YEAR
    , EMP_TOT_YEAR
    , EMP_MONTH
    , EMP_TOT_MONTH
)
VALUES(
    #{ code }
    , #{ location }
    , #{ qual }
    , #{ salary }
    , #{ year }
    , #{ totalYear }
    , #{ month }
    , #{ totalMonth }
)

mapper.select:
SELECT A.USER_CODE , A.USER_ID , A.USER_PWD , A.USER_NAME , A.USER_PHONE , A.USER_ROLE , A.USER_PIC , A.USER_GENDER , A.USER_BIRTH , A.USER_ADDRESS , A.USER_ETC , B.PHY_HEIGHT , B.PHY_WEIGHT , B.PHY_BMI , C.EMP_LOCATION , C.EMP_YEAR , C.EMP_MONTH , C.EMP_TOT_YEAR , C.EMP_TOT_MONTH , C.EMP_QUAL , C.EMP_SALARY FROM USERDB A JOIN PHYSICALDB B ON A.USER_CODE = B.USER_CODE JOIN EMPLOYEEDB C ON A.USER_CODE = C.USER_CODE
WHERE A.USER_NAME = #{ text }
WHERE A.USER_CODE = #{ text }

mapper.update:
UPDATE USERDB

    USER_CODE = #{ code }

        , USER_ID = #{ id }


        , USER_PWD = #{ pwd }


        , USER_NAME = #{ name }


        , USER_PIC = #{ pic }


        , USER_PHONE = #{ phone }


        , USER_BIRTH = #{ birth }


        , USER_ADDRESS = #{ address }


        , USER_ETC = #{ etc }


WHERE USER_CODE = #{ code }

UPDATE PHYSICALDB

    USER_CODE = #{ code }

        , PHY_HEIGHT = #{ height }


        , PHY_WEIGHT = #{ weight }


        , PHY_BMI = #{ bmi }


WHERE USER_CODE = #{ code }

UPDATE EMPLOYEEDB

    USER_CODE = #{ code }

        , EMP_LOCATION = #{ location }


        , EMP_MONTH = #{ month }


        , EMP_TOT_MONTH = #{ totalMonth }


        , EMP_YEAR = #{ year }


        , EMP_TOT_YEAR = #{ totalYear }


        , EMP_QUAL = #{ qual }


        , EMP_SALARY = #{ salary }



WHERE USER_CODE = #{ code }
